# Put them all together 
state_list <- list(
  "alabama" =  al, "alaska" = ak, "california" = ca, "connecticut" = ct, "delaware" = de, 
  "florida" = fl, "georgia" = ga, "hawaii" = hi, "louisiana" = la, "massachusetts" = ma,
  "maryland" = md, "maine" = me, "mississippi" = ms, "north carolina" = nc, "new hampshire" = nh, 
  "new jersey" = nj, "new york" = ny, "oregon" = or, "pennsylvania" = pa, "rhode island" = ri,
  "south carolina" = sc, "texas" = tx, "virginia" = va, "washington" = wa)

# Single table
noaa_station_key <- map_dfr(state_list, function(state){
  state %>% 
    mutate(Id = as.character(Id)) %>% 
    rename_all(tolower)
}, .id = "state") %>% 
  drop_na()

# Export it
write_csv(noaa_station_key, here("data/noaa_tide_stations.csv"))
